SELECT airportname, geo.lat AS high_latitude FROM `travel-sample`
LET items = ARRAY item FOR item IN geo.lat WHEN item.lat > 70 END
WHERE airportname IS NOT NULL
ORDER BY geo.lat DESC
LIMIT 5;This
